  };

  const fabricPriceMap = {
    'pinkit kukat': 10.0,
    värikäs: 15.0,
    keltainen: 12.0,
    'oma kangas': 0.0,
  };

  const productPrice = productPriceMap[productType] || 0.0;
  const fabricPrice = fabricPriceMap[fabric] || 0.0;
  const shippingPrice = 6.9;
  const calculatedTotal = productPrice + fabricPrice + shippingPrice;

  setTotal(calculatedTotal.toFixed(2));
};
