import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { getOrders, getUser } from './api';

function ThankYou() {
  const [orders, setOrders] = useState([]);
  const [userId, setUserId] = useState(null); // Define userId state variable
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const sessionId = searchParams.get('sessionId');

  useEffect(() => {
    if (sessionId) {
      // Fetch user ID based on the session ID
      const fetchUserId = async () => {
        try {
          const userData = await getUser(sessionId);
          setUserId(userData.id);
        } catch (error) {
          console.error('Error fetching user ID:', error);
        }
      };

      fetchUserId();
    }
  }, [sessionId]); // Pass sessionId as a dependency to useEffect

  useEffect(() => {
    if (sessionId && userId) {
      // Fetch orders based on the session ID and user ID
      const fetchOrders = async () => {
        try {
          const ordersData = await getOrders(sessionId);
          setOrders(ordersData.filter(order => order.userId === userId));
        } catch (error) {
          console.error('Error fetching orders:', error);
        }
      };

      fetchOrders();
    }
  }, [sessionId, userId]); // Pass sessionId and userId as dependencies to useEffect

  return (
    <div>
      <h1>Kiitos tilauksestanne!</h1>

      {/* Display order information for each order */}
      {orders.map((order, index) => (
        <div key={index} className="thankyou-container">
          <h2>Tilauksenne:</h2>
          <p>Tuote: {order.productType}</p>
          <p>Kangas: {order.fabric}</p>
          <p>Yhteishinta: {order.totalCost} â‚¬</p>
          <br />

          <h2>Laskutustiedot:</h2>
          <p>Puh: {order.phoneNumber}</p>
          <p>Email: {order.email}</p>
        </div>
      ))}

      <div className="payment-info-container">
        {/* Your existing thank you information */}
      </div>

      <footer>
        <p>&copy; Hurmaava Design Studio</p>
      </footer>
    </div>
  );
}

export default ThankYou;
