import React, { useState, useEffect } from 'react';
import { getOrders, getUser } from './api';

function ThankYou() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Fetch orders when the component mounts
    const fetchOrders = async () => {
      try {
        // Get the session ID from the URL parameters
        const urlSearchParams = new URLSearchParams(window.location.search);
        const sessionId = urlSearchParams.get('sessionId');

        // Check if sessionId is present
        if (!sessionId) {
          console.error('Session ID is missing.');
          return;
        }

        const ordersData = await getOrders(sessionId);
        setOrders(ordersData);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  return (
    <div>
      <h1>Kiitos tilauksestanne!</h1>

      {/* Display order information for each order */}
      {orders.length > 0 ? (
        orders.map((order, index) => (
          <div key={index} className="thankyou-container">
            <h2>Tilauksenne:</h2>
            <p>Tuote: {order.productType}</p>
            <p>Kangas: {order.fabric}</p>
            <p>Yhteishinta: {order.totalCost} â‚¬</p>
            <br />

            <h2>Laskutustiedot:</h2>
            <p>Puh: {order.phoneNumber}</p>
            <p>Email: {order.email}</p>
          </div>
        ))
      ) : (
        <p>Ei tilauksia.</p>
      )}

      <div className="payment-info-container">
        {/* Your existing thank you information */}
      </div>

      <footer>
        <p>&copy; Hurmaava Design Studio</p>
      </footer>
    </div>
  );
}

export default ThankYou;
