import React, { useState } from 'react';

function OrderForm() {
  // Define states to manage selected product type, fabric, and the total cost
  const [productType, setProductType] = useState('');
  const [fabric, setFabric] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);

  // Define the product type and fabric image maps
  const productTypeImageMap = {
    takki: "./images/takki.JPG",
    liivi: "./images/liivi.JPG"
  };

  const fabricImageMap = {
    "pinkit kukat": "./images/pinkitkukat.jpg",
    värikäs: "./images/värikäs.jpg",
    keltainen: "./images/keltainen.jpeg"
  };

  // Event handler for product type change
  const handleProductTypeChange = (event) => {
    const selectedProductType = event.target.value;
    setProductType(selectedProductType);
    updateFeaturedImage(selectedProductType, fabric);
  };

  // Event handler for fabric change
  const handleFabricChange = (event) => {
    const selectedFabric = event.target.value;
    setFabric(selectedFabric);
    updateFeaturedImage(productType, selectedFabric);
  };

  // Function to calculate and update the total price
  const calculateTotal = () => {
    const productPrice = parseFloat(document.getElementById("productType").value);
    const fabricPrice = parseFloat(document.getElementById("fabric").value);
    const shippingPrice = 6.9;
    const total = productPrice + fabricPrice + shippingPrice;

    setTotalPrice(total.toFixed(2));
  };

  // Function to update the featured image
  const updateFeaturedImage = (selectedProductType, selectedFabric) => {
    const featuredImage = document.getElementById("featured");
    const productTypeImage = productTypeImageMap[selectedProductType];
    const fabricImage = fabricImageMap[selectedFabric];

    if (fabricImage) {
      featuredImage.src = fabricImage;
    } else if (productTypeImage) {
      featuredImage.src = productTypeImage;
    }
  };

  return (
    <div className="order-form-container">
      <div id="product-img-container">
        <img id="featured" src="./images/takki.JPG" alt="Product" />
        <div id="slide-wrapper">
          <img id="slideLeft" className="arrow" src="./images/arrow/left.jpg" alt="Left Arrow" />
          <div id="slider">
            <img className="thumbnail active" src="./images/takki.JPG" alt="Thumbnail" />
            <img className="thumbnail" src="./images/liivi.JPG" alt="Thumbnail" />
            <img className="thumbnail" src="./images/pinkitkukat.jpg" alt="Thumbnail" />
            <img className="thumbnail" src="./images/värikäs.jpg" alt="Thumbnail" />
            <img className="thumbnail" src="./images/keltainen.jpeg" alt="Thumbnail" />
          </div>
          <img id="slideRight" className="arrow" src="./images/arrow/right.jpg" alt="Right Arrow" />
        </div>
      </div>
      <div className="form-container">
        <form id="order-form" action="/submit-order" method="post">
          <label htmlFor="productType">Tyyli:</label>
          <select id="productType" name="productType" required onChange={calculateTotal}>
            <option value="" data-price="0.00">Valitse tuote</option>
            <option value="takki" data-price="100.00">Takki - 100.00€</option>
            <option value="liivi" data-price="65.00">Liivi - 65.00€</option>
          </select>
          <br />
          <label htmlFor="fabric">Kangas:</label>
          <select id="fabric" name="fabric" required onChange={calculateTotal}>
            <option value="" data-price="0.00">Valitse kangas</option>
            <option value="pinkit kukat" data-price="10.00">Pinkit kukat - 10.00€</option>
            <option value="värikäs" data-price="15.00">Värikäs - 15.00€</option>
            <option value="keltainen" data-price="12.00">Keltainen - 12.00€</option>
            <option value="oma kangas" data-price="0.00">Oma kangas - 0.00€</option>
          </select>
          <br />
          <label htmlFor="size">Koko:</label>
          <select id="size" name="size" required>
            <option value="ONE SIZE">ONE SIZE</option>
          </select>
          <br />
          <label htmlFor="phoneNumber">Puh:</label>
          <input type="tel" id="phoneNumber" name="phoneNumber" required />
          <br />
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" required />
          <br />
          <div className="total">
            <h4>Tilauksesi:</h4>
            <p>Työ:</p>
            <p>Kangas:</p>
            <p>Postikulut:</p>
            <hr />
            <p>Yhteensä</p>
          </div>
          <div className="button-container">
            <button type="submit">Lähetä tilaus</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default OrderForm;
