import React, { useState } from 'react';

function OrderForm() {
  const [productType, setProductType] = useState('');
  const [fabric, setFabric] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);
  const [featuredImage, setFeaturedImage] = useState('./images/takki.JPG');

  const productTypeImageMap = {
    takki: "./images/takki.JPG",
    liivi: "./images/liivi.JPG"
  };

  const fabricImageMap = {
    "pinkit kukat": "./images/pinkitkukat.jpg",
    värikäs: "./images/värikäs.jpg",
    keltainen: "./images/keltainen.jpeg"
  };

  const handleProductTypeChange = (event) => {
    const selectedProductType = event.target.value;
    setProductType(selectedProductType);
    updateFeaturedImage(selectedProductType, fabric);
  };

  const handleFabricChange = (event) => {
    const selectedFabric = event.target.value;
    setFabric(selectedFabric);
    updateFeaturedImage(productType, selectedFabric);
  };

  const calculateTotal = () => {
    const productPrice = parseFloat(document.getElementById("productType").value);
    const fabricPrice = parseFloat(document.getElementById("fabric").value);
    const shippingPrice = 6.9;
    const total = productPrice + fabricPrice + shippingPrice;

    setTotalPrice(total.toFixed(2));
  };

  const updateFeaturedImage = (selectedProductType, selectedFabric) => {
    const productTypeImage = productTypeImageMap[selectedProductType];
    const fabricImage = fabricImageMap[selectedFabric];

    if (fabricImage) {
      setFeaturedImage(fabricImage);
    } else if (productTypeImage) {
      setFeaturedImage(productTypeImage);
    }
  };

  return (
    <div className="order-form-container">
      <div id="product-img-container">
        <img id="featured" src={featuredImage} alt="Product" />
        {/* ... rest of your code */}
      </div>
      <div className="form-container">
        <form id="order-form" action="/submit-order" method="post">
          {/* ... rest of your code */}
        </form>
      </div>
    </div>
  );
}

export default OrderForm;
