// Dashboard.js
import React, { useState, useEffect } from 'react';

function Dashboard() {
  const [orders, setOrders] = useState([]);

  // Function to generate a secure random session ID
  const generateSessionId = () => {
    const array = new Uint32Array(1);
    window.crypto.getRandomValues(array);
    return array[0].toString(16);
  };

  useEffect(() => {
    // Generate a new session ID
    const sessionId = generateSessionId();

    // Fetch orders from the backend using the generated session ID
    fetch(`http://localhost:3002/orders?sessionId=${sessionId}`)
      .then(response => response.json())
      .then(data => setOrders(data))
      .catch(error => console.error('Error fetching orders:', error));
  }, []); // Empty dependency array to ensure the effect runs only once on component mount

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <p>Welcome to the admin dashboard!</p>
      <h2>Orders</h2>
      <ul>
        {orders.map(order => (
          <li key={order._id}>
            Customer: {order.phoneNumber}, Product: {order.productType}, Fabric: {order.fabric}, Total Cost: {order.totalCost}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
